import { Component, OnInit } from '@angular/core';
import { CategoryService } from '../category.service'; // Import your category service
import { Category } from '../../models/category.model'; // Import the category model

@Component({
  selector: 'app-category-add',
  templateUrl: './category-add.component.html',
  styleUrls: ['./category-add.component.css']
})
export class CategoryAddComponent implements OnInit {
  newCategory: Category = {
    id: 0, // You may need to generate a unique ID
    name: '',
    description: ''
  };

  constructor(private categoryService: CategoryService) {}

  ngOnInit(): void {}

  addCategory(): void {
    this.categoryService.addCategory(this.newCategory).subscribe(() => {
      // Category added successfully, handle success or navigation here
      console.log('Category added:', this.newCategory);
    });
  }
}
