import { Component, OnInit, ViewChild } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Category } from '../category.model';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';
import { NgForm } from '@angular/forms'; 

@Component({
  selector: 'app-category-add',
  templateUrl: './category-add.component.html',
  styleUrls: ['./category-add.component.css']
})
export class CategoryAddComponent implements OnInit {
  newCategory: Category = {
    id: 0,
    name: ''
  };

  @ViewChild('categoryForm') categoryForm!: NgForm; 

  constructor(private http: HttpClient, private authService: AuthService, private router: Router) {}

  ngOnInit(): void {}

  addCategory(): void {
    if (this.categoryForm.invalid) {
      // Form is invalid, don't submit
      return;
    }

    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    const apiUrl = 'http://127.0.0.1:8000/api/add-category';
    this.http.post(apiUrl, this.newCategory, { headers }).subscribe(
      (response: any) => {
        console.log('Category added:', response);
        this.router.navigate(['/products']);
      },
      (error: any) => {
        console.error('Error adding category:', error);
      }
    );
  }
  cancel(): void {
    // Redirect to the /products page
    this.router.navigate(['/products']);
  }
}
<div class="card">
  <div class="card-header">
    Add New Category
  </div>
  <div class="card-body">
    <form novalidate #categoryForm="ngForm" (ngSubmit)="addCategory()">
      <div class="form-group row mb-2">
        <label class="col-md-2 col-form-label" for="name">Category Name:</label>
        <div class="col-md-8">
          <input class="form-control" type="text" id="name" [(ngModel)]="newCategory.name" name="name" required>
          <!-- Add a validation message -->
          <div *ngIf="categoryForm.controls.name.invalid && categoryForm.controls.name.touched" class="text-danger">
            Category name is required.
          </div>
        </div>
      </div>
      <div class="form-group row mb-2">
        <div class="offset-md-2 col-md-4">
          <button class="btn btn-primary mr-3" style="width:80px;" type="submit" [disabled]="categoryForm.invalid">
            Save
          </button>
          <button class="btn btn-secondary" style="width:80px;" type="button" (click)="cancel()">
            Cancel
          </button>
        </div>
      </div>
    </form>
  </div>
</div>
