import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http'; // Import HttpClient
import { Categ

@Component({
  selector: 'app-category-add',
  templateUrl: './category-add.component.html',
  styleUrls: ['./category-add.component.css']
})
export class CategoryAddComponent implements OnInit {
  newCategory: Category = {
    id: 0, // You may need to generate a unique ID
    name: '',
    description: ''
  };

  constructor(private http: HttpClient) {} // Inject HttpClient

  ngOnInit(): void {}

  addCategory(): void {
    const apiUrl = 'http://your-api-url/add-category'; // Replace with your API endpoint
    this.http.post(apiUrl, this.newCategory).subscribe(
      (response: any) => {
        // Category added successfully, handle success or navigation here
        console.log('Category added:', response);
      },
      (error: any) => {
        // Handle error here
        console.error('Error adding category:', error);
      }
    );
  }
}
