import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'pm-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit {
  products: any[] = []; // Array to store fetched products

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    // Replace 'your-api-endpoint' with the actual URL of your Symfony API
    const apiUrl = 'http://127.0.0.1:8000/category/2'; // Replace '1' with the category ID you want to fetch

    // Make an HTTP GET request to fetch products by category
    this.http.get(apiUrl).subscribe((data: any) => {
      this.products = data; // Assuming your API returns an array of products
    });
  }
}
