import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http'; 
import { Category } from '../category.model';

@Component({
  selector: 'app-category-add',
  templateUrl: './category-add.component.html',
  styleUrls: ['./category-add.component.css']
})
export class CategoryAddComponent implements OnInit {
  newCategory: Category = {
    id: 0, 
    name: ''
  };

  constructor(private http: HttpClient) {} 

  ngOnInit(): void {}

  addCategory(): void {
    const apiUrl = 'http://127.0.0.1:8000/api/add-category'; // Replace with your API endpoint
    this.http.post(apiUrl, this.newCategory).subscribe(
      (response: any) => {
        // Category added successfully, handle success or navigation here
        console.log('Category added:', response);
      },
      (error: any) => {
        // Handle error here
        console.error('Error adding category:', error);
      }
    );
  }
}

<div>
    <h2>Add a New Category</h2>
    <form (ngSubmit)="addCategory()">
      <div class="form-group">
        <label for="name">Name:</label>
        <input type="text" id="name" [(ngModel)]="newCategory.name" name="name" required>
      </div>
      <button type="submit" class="btn btn-primary">Add Category</button>
    </form>
  </div>
