import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'pm-admin',
  template: `
    <iframe [src]="iframeUrl" width="100%" height="600"></iframe>
  `,
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  iframeUrl: SafeResourceUrl | undefined; // Declare the variable with SafeResourceUrl type

  constructor(
    private authService: AuthService,
    private router: Router,
    private sanitizer: DomSanitizer,
    private http: HttpClient // Inject HttpClient
  ) {}

  ngOnInit(): void {
    const token = this.authService.getToken();
    const adminUrl = 'http://127.0.0.1:8000/api/admin'; // Replace with your EasyAdminBundle URL

    // Sanitize the URL
    this.iframeUrl = this.sanitizer.bypassSecurityTrustResourceUrl(`${adminUrl}?token=${token}`);

    // Send an HTTP request with the JWT token in the header
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);
    this.http.get(adminUrl, { headers }).subscribe(
      (response: any) => {
        this.iframeUrl = this.sanitizer.bypassSecurityTrustResourceUrl(`${adminUrl}?token=${token}`);

        // Handle the response as needed
      },
      (error) => {
        console.error('Error fetching admin data', error);
        if (error.status === 401) {
          this.router.navigate(['/login']);
        }
      }
    );
  }
}
