import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'pm-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent {

  adminData:any;

  constructor(private http: HttpClient, private authService: AuthService, private router: Router) {}

  ngOnInit(): void {

    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    const adminUrl = 'http://127.0.0.1:8000/admin';

    this.http.get(adminUrl, {headers}).subscribe(
      (response: any) => {
        this.adminData = response;
      },
      (error) => {
        console.error('Error fetching admin data', error);
        if (error.status === 401) {
          this.router.navigate(['/login']);
        }
      }
    );
  }

}
<div class="admin-container">
    <h2>Welcome to the Admin Dashboard</h2>

    <div *ngIf="adminData">
      <!-- Display admin dashboard data here -->
      <div class="admin-info">
        <h3>User Information:</h3>
        <p><strong>Username:</strong> {{ adminData.username }}</p>
        <p><strong>Email:</strong> {{ adminData.email }}</p>
        <!-- Add more data fields as needed -->
      </div>
    </div>

    <div *ngIf="!adminData">
      <p>Loading...</p>
      <!-- You can also display an error message here if needed -->
    </div>
  </div>
