import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css'],
})
export class CategoryComponent implements OnInit {
  categories: any[] = []; 
  selectedCategoryId: number | null = null;
  selectedCategoryName: string = ''; 
  products: any[] = []; 

  constructor(private http: HttpClient, private authService: AuthService) { }

  ngOnInit(): void {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    // Fetch categories from your API (replace with your actual API endpoint)
    this.http.get('http://127.0.0.1:8000/api/category', { headers }).subscribe((data: any) => {
      this.categories = data;
    });
  }

  onCategoryChange(): void {
    if (this.selectedCategoryId !== null) {
      const categoryId = +this.selectedCategoryId; 
      const apiUrl = `http://127.0.0.1:8000/api/category/${categoryId}`;
      const token = this.authService.getToken();
      const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);  

      this.http.get(apiUrl, { headers }).subscribe((data: any) => {
        this.products = data;
      });

      // Update the selectedCategoryName based on the selectedCategoryId
      const selectedCategory = this.categories.find(category => category.id === categoryId);
      this.selectedCategoryName = selectedCategory ? selectedCategory.name : '';
    } else {
      // If no category is selected, clear the products array and reset the selectedCategoryName
      this.products = [];
      this.selectedCategoryName = '';
    }
  }
}
<div class="card">
    <div class="card-header">
      Products by Category
    </div>
    <div class="container mt-4">
    <div class="row">
        <div class="col-md-4">
        <!-- Category selection dropdown -->
        <div class="form-group row mb-4">
            <label for="categorySelect" class="text-primary">Select Category:</label>
            <div class="col_md-4">
            <select class="form-control custom-select" id="categorySelect" [(ngModel)]="selectedCategoryId" (change)="onCategoryChange()">
            <option [ngValue]="null">Select a Category</option>
            <option *ngFor="let category of categories" [value]="category.id">{{ category.name }}</option>
            </select>
        </div>
        </div>
        </div>
    </div>
    <div class="row">
        <div class="col-md-12">
            <div class="col-md-6">
                <h5>Products in {{ selectedCategoryName || 'Selected Category' }}</h5>
              </div>
        <div class="table-responsive">
            <table class="table table-bordered table-striped">
            <thead class="thead-dark">
                <tr>
                <th scope="col">Product Name</th>
                <th scope="col">Product Code</th>
                <th scope="col">Price</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let product of products">
                <td>{{ product.productName }}</td>
                <td>{{ product.productCode }}</td>
                <td>{{ product.price | currency:'USD':true:'1.2-2' }}</td>
                </tr>
            </tbody>
            </table>
        </div>
        </div>
    </div>
    </div>
</div>
