import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css'],
})
export class CategoryComponent implements OnInit {
  categories: any[] = []; // Array to store fetched categories
  selectedCategoryId: number | null = null; // Store the selected category ID
  products: any[] = []; // Array to store fetched products

  constructor(private http: HttpClient, private authService: AuthService) { }

  ngOnInit(): void {

    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);
    // Fetch categories from your API (replace with your actual API endpoint)
    this.http.get('http://127.0.0.1:8000/api/category', { headers }).subscribe((data: any) => {
      this.categories = data;
    });
  }

  onCategoryChange(): void {
    // Fetch products for the selected category
    if (this.selectedCategoryId !== null) {
      // Replace 'your-api-endpoint' and 'categoryId' with your actual API endpoint and parameter
      const apiUrl = `http://127.0.0.1:8000/api/category/${this.selectedCategoryId}`;

      this.http.get(apiUrl).subscribe((data: any) => {
        this.products = data;
      });
    } else {
      // If no category is selected, clear the products array
      this.products = [];
    }
  }
}
