import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';

@Component({
  selector: 'pm-view-category',
  templateUrl: './view-category.component.html',
  styleUrls: ['./view-category.component.css']
})
export class ViewCategoryComponent {
  categories: any[] = []; 

  constructor(private http:HttpClient, private authService:AuthService, private router:Router){}

  ngOnInit(): void {
    this.loadCategories();
  }

  loadCategories() {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);
    this.http.get<any[]>('http://127.0.0.1:8000/api/category', {headers}).subscribe((data: any) => {
      this.categories = data;
    });
  }

  deleteCategory(categoryId: number) {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);
    this.http.delete<void>(`http://127.0.0.1:8000/api/delete-category/${categoryId}`, {headers} ).subscribe(() => {
      // After successful deletion, reload the categories
      alert("Category Deleted Successfully")
      this.loadCategories();

    });
  }

  onBack():void{
    this.router.navigate(['/products']);
  }



}
<div class="card">
    <div class="card-header">
        Category List
    </div>

    <div class="card-body">
        <div class="table-responsive">
            <table class=" table mb-0">
                <thead>
                    <tr>
                        <th>Category Id</th>
                        <th>Category Name</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let category of categories">
                        <td>{{category.id}}</td>
                        <td>{{category.name}}</td>
                        <td><button class="btn btn-outline-primary btn-sm"
                            (click)="deleteCategory(category.id)">
                            Delete
                        </button></td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>


<div class="row mt-4">
    <div class="col-md-4">
      <button class="btn btn-outline-secondary mr-3"
              style="width:80px"
              (click)="onBack()">
        <i class="fa fa-chevron-left"></i> Back
      </button>
      <button class="btn btn-outline-primary"
              style="width:100px"
              [routerLink]="['/categories/add']">
        Add New
      </button>
    </div>
  </div>
</div>
