import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { AuthService } from '../auth.service';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
}

@Component({
  selector: 'app-view-cart',
  templateUrl: './viewcart.component.html',
  styleUrls: ['./viewcart.component.css']
})
export class ViewCartComponent implements OnInit {
  username: string | undefined;
  products: Product[] = [];

  constructor(private http: HttpClient, private authService:AuthService) {}

  ngOnInit(): void {
    this.fetchCartData();
  }

  fetchCartData() {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    this.http.get<any>('http://127.0.0.1:8000/api/cart/view-cart', { headers }).subscribe(data => {
      this.username = data.username;
      this.products = data.products;
    });
  }


  removeProduct(productId: number) {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    this.http.post<any>(`http://127.0.0.1:8000/api/cart/remove-product/${productId}`, {}, { headers }).subscribe(
      () => {
        alert('Product removed from cart.');
        this.fetchCartData();
      },
      (error) => {
        console.error('Error removing product:', error);
        alert("Something went wrong");
      }
    );
  }


  fetchImage(imageUrl: string): void {
    this.http.get(imageUrl, { responseType: 'blob' }).subscribe(response => {
      const reader = new FileReader();
      reader.onloadend = () => {
        const imageElement = document.getElementById('productImage') as HTMLImageElement;
        imageElement.src = reader.result as string;
      };
      reader.readAsDataURL(response);
    });
  }

}

<div class="card">
    <div class="card-header">
        Cart for {{ username }}
    </div>

    <div class="card-body">
        <div class="table-responsive">
            <table class="table mb-0">
            <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Product Price</th>
                    <th>Product Description</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let product of products">
                    <td>{{product.name}}</td>
                    <td>{{product.price | currency:"USD":"symbol":"1.2-2"}}</td>
                    <td>{{product.description}}</td>
                    <td>
                        <button class="btn btn-danger"
                            style="width:100px"
                            (click)="removeProduct(product.id)">
                            Remove
                        </button>
                    </td>
                </tr>
            </tbody>
        </table>
        </div>
    </div>
  </div>
