import { Component, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { AuthService } from '../auth.service';

interface Product {
  id: number;
  name: string;
  price: number;
  description: string;
}

@Component({
  selector: 'app-view-cart',
  templateUrl: './viewcart.component.html',
  styleUrls: ['./viewcart.component.css']
})
export class ViewCartComponent implements OnInit {
  username: string | undefined;
  products: Product[] = [];

  constructor(private http: HttpClient, private authService:AuthService) {}

  ngOnInit(): void {
    this
    });
  }

  fetchCartData() {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    this.http.get<any>('http://127.0.0.1:8000/api/cart/view-cart', { headers }).subscribe(data => {
      this.username = data.username;
      this.products = data.products;
    });
  }

  removeProduct(productId: number) {
    const token = this.authService.getToken();
    const headers = new HttpHeaders().set('Authorization', `Bearer ${token}`);

    // Send a request to your Symfony endpoint to remove the product
    this.http.post<any>('http://127.0.0.1:8000/api/cart/update-cart', {
      action: 'remove',
      productId: productId
    }, { headers }).subscribe(data => {
      // Handle success or error response here
      // You can update the cart data after a successful removal if needed
      this.fetchCartData();
    });
  }

}

<div class="card">
    <div class="card-header">
        Cart for {{ username }}
    </div>

    <div class="card-body">
        <div class="table-responsive">
            <table class="table mb-0">
            <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Product Price</th>
                    <th>Product Description</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let product of products">
                    <td>{{product.name}}</td>
                    <td>{{product.price | currency:"USD":"symbol":"1.2-2"}}</td>
                    <td>{{product.description}}</td>
                    <td>
                        <button class="btn btn-danger"
                            style="width:80px"
                            (click)="removeProduct(product.id)">
                            Remove
                        </button>
                    </td>
                </tr>
            </tbody>
        </table>
        </div>
    </div>
    <div class="row mt-4">
        <div class="col-md-4">
            <button class="btn btn-outline-primary"
                style="width:80px"
                [routerLink]="['/edit-cart']">
          Edit
        </button>
        </div>
    </div>
  </div>
