import { Component } from '@angular/core';
import { AuthService } from './auth.service'; // Import AuthService

@Component({
  selector: 'pm-root',
  template: `
    <nav class='navbar navbar-expand navbar-light bg-light'>
      <a style="font-weight:bold;" class='navbar-brand'>{{pageTitle}}</a>
      <ng-container *ngIf="isAuthenticated; else notAuthenticated">
        <!-- Display this when the user is authenticated -->
        <ul class='navbar-nav'>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/welcome']">Home</a></li>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/products']">Products</a></li>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/products/0/edit']">Add Product</a></li>
          <li class='nav-item'><a class='nav-link' (click)="logout()">Logout</a></li>
        </ul>
      </ng-container>
      <ng-template #notAuthenticated>
        <!-- Display this when the user is not authenticated -->
        <ul class='navbar-nav'>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/welcome']">Home</a></li>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/login']">Login</a></li>
          <li class='nav-item'><a class='nav-link' routerLinkActive='active' [routerLink]="['/signUp']">Register</a></li>
        </ul>
      </ng-template>
    </nav>
    <div class='container'>
      <router-outlet></router-outlet>
    </div>
    `,
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  pageTitle = 'Tech & Mech';
  isAuthenticated = true;

  constructor(private authService: AuthService) {}

  ngOnInit() {
    // Check if the user is authenticated when the app loads
    this.isAuthenticated = this.authService.isAuthenticatedUser();
  }

  logout() {
    // Call the logout method from AuthService
    this.authService.logout();
    // Update the isAuthenticated status to false
    this.isAuthenticated = false;
  }
}
