import { Component, OnInit } from '@angular/core';

interface Product {
  id: number;
  name: string;
}

interface Cart {
  products: Product[];
}

@Component({
  selector: 'pm-edit-cart',
  templateUrl: './edit-cart.component.html',
  styleUrls: ['./edit-cart.component.css']
})
export class EditCartComponent implements OnInit{
  cart: Cart = { products: [] };
  productId: number;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchCart();
  }

  fetchCart(): void {
    // Make an HTTP GET request to fetch the user's cart from Symfony controller
    this.http.get<Cart>('/update-cart').subscribe((data) => {
      this.cart = data;
    });
  }

  addProduct(): void {
    if (this.productId) {
      // Make an HTTP POST request to add a product to the cart
      this.http.post('/update-cart', { action: 'add', productId: this.productId }).subscribe(() => {
        this.fetchCart();
      });
    }
  }

  removeProduct(productId: number): void {
    // Make an HTTP POST request to remove a product from the cart
    this.http.post('/update-cart', { action: 'remove', productId }).subscribe(() => {
      this.fetchCart();
    });
  }
}
