import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  iframeUrl: SafeResourceUrl | undefined;

  constructor(private authService: AuthService, private sanitizer: DomSanitizer) {}

  ngOnInit(): void {
    const token = this.authService.getToken();
    const adminUrl = 'http://your-symfony-backend-url/admin'; // Replace with your EasyAdminBundle URL

    // Sanitize the URL
    this.iframeUrl = this.sanitizer.bypassSecurityTrustResourceUrl(`${adminUrl}?token=${token}`);
  }
}
