import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class CartService {
  private apiUrl = 'http://127.0.0.1:8000/api/cart/add-product'; // Replace with your Symfony API endpoint URL

  constructor(private http: HttpClient, authService: Auth) {}

  addToCart(productId: number): Observable<any> {
    // Send a POST request to add the product to the cart
    return this.http.post(this.apiUrl, { product_id: productId });
  }
}
